import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { StyleSheet, Text, View, ViewPropTypes, } from 'react-native';
import moment from 'moment';
import Color from './Color';
import { TIME_FORMAT } from './Constant';
const containerStyle = {
    marginLeft: 10,
    marginRight: 10,
    marginBottom: 5,
};
const textStyle = {
    fontSize: 10,
    backgroundColor: 'transparent',
    textAlign: 'right',
};
const styles = {
    left: StyleSheet.create({
        container: Object.assign({}, containerStyle),
        text: Object.assign({ color: Color.timeTextColor }, textStyle),
    }),
    right: StyleSheet.create({
        container: Object.assign({}, containerStyle),
        text: Object.assign({ color: Color.white }, textStyle),
    }),
};
export default class Time extends Component {
    render() {
        const { position, containerStyle, currentMessage, timeFormat, textStyle, } = this.props;
        if (!!currentMessage) {
            return (<View style={[
                styles[position].container,
                containerStyle && containerStyle[position],
            ]}>
          <Text style={[
                styles[position].text,
                textStyle && textStyle[position],
            ]}>
            {moment(currentMessage.createdAt)
                .locale(this.context.getLocale())
                .format(timeFormat)}
          </Text>
        </View>);
        }
        return null;
    }
}
Time.contextTypes = {
    getLocale: PropTypes.func,
};
Time.defaultProps = {
    position: 'left',
    currentMessage: {
        createdAt: null,
    },
    containerStyle: {},
    textStyle: {},
    timeFormat: TIME_FORMAT,
    timeTextStyle: {},
};
Time.propTypes = {
    position: PropTypes.oneOf(['left', 'right']),
    currentMessage: PropTypes.object,
    containerStyle: PropTypes.shape({
        left: ViewPropTypes.style,
        right: ViewPropTypes.style,
    }),
    textStyle: PropTypes.shape({
        left: PropTypes.any,
        right: PropTypes.any,
    }),
    timeFormat: PropTypes.string,
    timeTextStyle: PropTypes.shape({
        left: PropTypes.any,
        right: PropTypes.any,
    }),
};
