import React, { Component } from "react";
import { Image, View,Text, ScrollView, } from "react-native";
import {
  Container,
  Content,
  Card,
  CardItem,
  Body,
  Header,
  Item,
  Input,
  Icon,
  Button
} from "native-base";

import styles from "./styles";
import {slideHaeder,data} from "./data"


const chatContactsImg = require("../../../assets/chatcontacts.png");


class Retailler extends Component {


  render() {
    const navigation = this.props.navigation;
    return (
      <Container>
        <Header searchBar >
          <Item style={{ borderRadius: 6, backgroundColor: "rgba(255,255,255,0.5)" }}>
            <Icon name="search" style={{ color: "#fff" }} />
            <Input
              placeholderTextColor={"rgba(255,255,255,0.5)"}
              placeholder="Search"
            />
          </Item>

          <Button
            transparent
            style={styles.headerBtn}
            onPress={() => navigation.openDrawer()}
          >
            <Image source={chatContactsImg} style={styles.sidebarIcon} />
          </Button>
          
        </Header>
        <Content style={styles.content}>
        <ScrollView 
        horizontal={true}
        showsHorizontalScrollIndicator={true}>
          <View style={{ ...styles.listViewBlock, alignItems: "center", justifyContent:'space-between', flexDirection:'row' }}>
 { slideHaeder.map (i=> (     <View style={{ marginLeft:6,borderWidth:1, borderColor:'white', paddingHorizontal:15, paddingVertical:6, borderRadius:5 }}>
    <Text style={{color:'white'}} onPress={()=> alert(i.name)}>{i.name}</Text>
          </View>
          
        ))   
        }
          </View>
          </ScrollView> 
         
          <View style={{flexDirection:'row', flexWrap:'wrap'}}>
            {data.map (i => (
              <Image source={i.img}  style={{ width: '32.5%', height: 200, margin:1 }} />
            ))}

          </View>
        </Content>
      </Container >
    );
  }
}

export default Retailler;
